INSERT INTO `pertanyaan` (`id`, `pertanyaan`, `pilihan`) VALUES
(1, 'Program keahlian melaksanakan kurikulum berdasarkan muatan Kurikulum Tingkat Satuan Pendidikan (KTSP)', '["Melaksanakan kurikulum berdasarkan 9 muatan KTSP","Melaksanakan kurikulum berdasarkan 8 muatan KTSP","Melaksanakan kurikulum berdasarkan 7 muatan KTSP","Melaksanakan kurikulum berdasarkan 6 atau kurang muatan KTSP","Tidak melaksanakan KTSP "]'),
(2, 'Program keahlian mengembangkan kurikulum bersama-sama Tim Pengembang Kurikulum berpedoman pada panduan penyusunan kurikulum yang dibuat oleh BSNP.', '["Bersama seluruh guru mata pelajaran dalam Program keahlian, Ketua program keahlian, Kepala Sekolah/Madrasah, Nara sumber dari Dunia Usaha atau Dunia Industri (DU/DI), konselor, komite sekolah/madrasah, dan penyelenggara lembaga pendidikan","Bersama seluruh guru mata pelajaran dalam program keahlian, Ketua program keahlian, Kepala Sekolah/Madrasah, Nara sumber dari Dunia Usaha atau Dunia Industri (DU/DI), dan konselor","Bersama seluruh guru mata pelajaran dalam program keahlian, Ketua program keahlian, Kepala Sekolah/Madrasah, dan Nara sumber dari Dunia Usaha atau Dunia Industri (DU/DI)","Bersama seluruh guru mata pelajaran dalam program keahlian, Ketua program keahlian, dan Kepala Sekolah/Madrasah","Tidak mengembangkan kurikulum "]'),
(3, 'Program keahlian mengembangkan kurikulum melalui mekanisme penyusunan KTSP.', '["Mekanisme pengembangan kurikulum dilakukan melalui 7 kegiatan pokok","Mekanisme pengembangan kurikulum dilakukan melalui 5 atau 6 kegiatan pokok","Mekanisme pengembangan kurikulum dilakukan melalui 3 atau 4 kegiatan pokok","Mekanisme pengembangan kurikulum dilakukan melalui 1 atau 2 kegiatan pokok","Tidak mengembangkan kurikulum   "]'),
(4, 'Program keahlian mengembangkan kurikulum dengan menggunakan prinsip pengembangan KTSP', '["Melaksanakan 7 prinsip pengembangan KTSP","Melaksanakan 5-6 prinsip pengembangan KTSP","Melaksanakan 3-4 prinsip pengembangan KTSP","Melaksanakan 1-2 prinsip pengembangan KTSP","Tidak mengembangkan kurikulum "]'),
(5, 'Program keahlian menyusun kurikulum muatan lokal dan kurikulum berbasis pendidikan karakter dengan melibatkan berbagai pihak.', '["Menyusun dengan melibatkan 7 pihak atau lebih","Menyusun dengan melibatkan 5-6 pihak","Menyusun dengan melibatkan 3-4 pihak","Menyusun dengan melibatkan 1-2 pihak","Tidak menyusun kurikulum muatan lokal "]'),
(6, 'Program keahlian melaksanakan program pengembangan diri dalam bentuk kegiatan ekstrakurikuler.', '["Melaksanakan 7 jenis atau lebih kegiatan ekstrakurikuler","Melaksanakan 5-6 kegiatan ekstrakurikuler","Melaksanakan 3-4 kegiatan ekstrakurikuler","Melaksanakan 1-2 kegiatan ekstrakurikuler","Tidak melaksanakan kegiatan ekstrakurikuler   "]'),
(7, 'Program keahlian melaksanakan program pengembangan diri dalam bentuk kegiatan konseling.', '["Melaksanakan 4 atau lebih jenis kegiatan konseling","Melaksanakan 3 jenis kegiatan konseling","Melaksanakan 2 jenis kegiatan konseling","Melaksanakan 1 jenis kegiatan konseling","Tidak melaksanakan kegiatan konseling "]'),
(8, 'Program keahlian melaksanakan program Praktik Kerja di Dunia Usaha / Industri (Prakerin) sesuai dengan ketentuan yang berlaku', '["Program dilaksanakan selama 3 bulan atau lebih dan mengikuti jam kerja karyawan/pegawai","Program dilaksanakan selama 3 bulan atau lebih, tetapi tidak mengikuti jam kerja karyawan/pegawai","Program dilaksanakan kurang dari 3 bulan, tetapi mengikuti jam kerja karyawan/pegawai","Program dilaksanakan kurang dari 3 bulan, dan tidak mengikuti jam kerja karyawan/pegawai","Program Praktik Kerja tidak dilaksanakan "]'),
(9, 'Program keahlian menerapkan kegiatan pembelajaran sesuai dengan ketentuan beban belajar yang tertuang pada Permendiknas Nomor 22 Tahun 2006.', '["Satu jam pembelajaran tatap muka selama 45 menit, jumlah jam pembelajaran per minggu minimal 36 jam, dan jumlah minggu efektif per tahun minimal 38 minggu","Satu jam pembelajaran tatap muka selama 45 menit, jumlah jam pembelajaran per minggu minimal 36 jam, dan jumlah minggu efektif per tahun kurang dari 38 minggu","Satu jam pembelajaran tatap muka selama 45 menit, jumlah jam pembelajaran per minggu kurang dari 36 jam, dan jumlah minggu efektif per tahun kurang dari 38 minggu","Satu jam pembelajaran tatap muka kurang dari 45 menit, jumlah jam pembelajaran per minggu kurang dari 36 jam, dan jumlah minggu efektif per tahun kurang dari 38 minggu","Tidak menerapkan ketentuan beban belajar yang ditetapkan Kemdikbud   "]'),
(10, 'Guru mata pelajaran memberikan penugasan terstruktur dan kegiatan mandiri tidak terstruktur untuk mencapai kompetensi yang diberikan kepada siswa maksimal 60% dari alokasi waktu tiap mata pelajaran.', '["91%-100% guru mata pelajaran memberikan penugasan terstruktur dan kegiatan mandiri tidak terstruktur","81%-90% guru mata pelajaran memberikan penugasan terstruktur dan kegiatan mandiri tidak terstruktur","71%-80% guru mata pelajaran memberikan penugasan terstruktur dan kegiatan mandiri tidak terstruktur","61%-70% guru mata pelajaran memberikan penugasan terstruktur dan kegiatan mandiri tidak terstruktur","Kurang dari 61% guru mata pelajaran memberikan penugasan terstruktur dan kegiatan mandiri tidak terstruktur "]'),
(11, 'Program keahlian melaksanakan proses pembelajaran mata pelajaran kewirausahaan berdasarkan ketentuan dalam struktur kurikulum.', '["Berdasarkan pada silabus, RPP, dan alokasi waktu 192 jam pelajaran","Berdasarkan pada silabus, tidak menggunakan RPP, dan alokasi waktu 192 jam pelajaran","Berdasarkan pada silabus, RPP, dan alokasi waktu kurang dari 192 jam pelajaran","Berdasarkan pada silabus, tidak ada RPP, dan alokasi waktu kurang dari 192 jam pelajaran","Tidak berdasarkan pada silabus, tidak ada RPP, dan alokasi waktu kurang dari 192 jam pelajaran "]'),
(12, 'Program keahlian memanfaatkan Teknologi Informasi dan Komunikasi (TIK) dalam pembelajaran seluruh mata pelajaran', '["Lebih dari 90% mata pelajaran memanfaatkan TIK.","81%-90% mata pelajaran memanfaatkan TIK.","71%-80% mata pelajaran memanfaatkan TIK.","61%-70% mata pelajaran memanfaatkan TIK.","Kurang dari 61% mata pelajaran memanfaatkan TIK.   "]'),
(13, 'KTSP disahkan oleh Kepala Sekolah/Madrasah dengan memperhatikan pertimbangan Komite Sekolah/Madrasah atau penyelenggara pendidikan yang disetujui oleh Dinas Pendidikan Provinsi atau Kanwil Kemenag.', '["KTSP disahkan oleh Kepala Sekolah/Madrasah dengan memperhatikan pertimbangan Komite Sekolah/Madrasah atau penyelenggara pendidikan, dan disetujui oleh Dinas Pendidikan Provinsi atau Kanwil Kemenag","KTSP disahkan oleh Kepala Sekolah/Madrasah dan disetujui oleh Dinas Pendidikan Provinsi atau Kanwil Kemenag","KTSP disahkan oleh Kepala Sekolah/Madrasah dengan memperhatikan pertimbangan Komite Sekolah/Madrasah atau penyelenggara pendidikan","KTSP disahkan oleh Kepala Sekolah/Madrasah","KTSP tidak disahkan "]'),
(14, 'Program keahlian mengembangkan silabus secara mandiri atau cara lainnya berdasarkan standar isi, standar kompetensi lulusan, dan panduan penyusunan KTSP.', '["Mengembangkan silabus melalui kelompok guru mata pelajaran dalam sebuah sekolah/madrasah","Mengembangkan silabus oleh masing-masing guru mata pelajaran","Mengembangkan silabus melalui kelompok guru dari beberapa sekolah/madrasah","Mengembangkan silabus dengan mengadopsi contoh yang sudah ada","Tidak mengembangkan silabus "]'),
(15, 'Guru mengembangkan silabus sesuai dengan langkah-langkah pengembangan', '["Guru melaksanakan 7 langkah","Guru melaksanakan 5-6 langkah","Guru melaksanakan 3-4 langkah","Guru melaksanakan 1-2 langkah","Guru tidak mengembangkan silabus   "]'),
(16, 'Program keahlian menjabarkan Standar Kompetensi (SK) dan Kompetensi Dasar (KD) ke dalam indikator-indikator untuk setiap mata pelajaran.', '["13 mata pelajaran atau lebih telah sesuai antara SK, KD, dan indikator-indikatornya","9-12 mata pelajaran telah sesuai antara SK, KD, dan indikatorindikatornya","5-8 mata pelajaran telah sesuai antara SK, KD, dan indikatorindikatornya","1-4 mata pelajaran telah sesuai antara SK, KD, dan indikatorindikatornya","Tidak ada mata pelajaran yang sesuai antara SK, KD, dan indikatorindikatornya "]'),
(17, 'Program keahlian menetapkan Kriteria Ketuntasan Minimal (KKM) 75% untuk setiap mata pelajaran.', '["6 atau lebih mata pelajaran dengan KKM 75% atau lebih","5 mata pelajaran dengan KKM 75% atau lebih","4 atau lebih mata pelajaran dengan KKM 75% atau lebih","3 atau lebih mata pelajaran dengan KKM 75% atau lebih","Kurang dari 3 mata pelajaran dengan KKM 75% atau lebih "]'),
(18, 'Program keahlian atau sekolah/madrasah memiliki kalender pendidikan yang memuat pengaturan waktu untuk kegiatan pembelajaran peserta didik selama satu tahun ajaran: (1) awal tahun pelajaran, (2) minggu efektif, (3) pembelajaran efektif, dan (4) hari libur.', '["Memuat 4 macam pengaturan waktu","Memuat 3 macam pengaturan waktu","Memuat 2 macam pengaturan waktu","Memuat 1 macam pengaturan waktu","Tidak memiliki kalender pendidikan   "]'),
(19, 'Setiap mata pelajaran memiliki Rencana Pelaksanaan Pembelajaran (RPP) dengan mengintegrasikan pendidikan karakter yang dijabarkan dari silabus.', '["13 mata pelajaran atau lebih memiliki RPP dengan mengintegrasikan pendidikan karakter yang dijabarkan dari silabus","9-12 mata pelajaran memiliki RPP dengan mengintegrasikan pendidikan karakter yang dijabarkan dari silabus","5-8 mata pelajaran memiliki RPP dengan mengintegrasikan pendidikan karakter yang dijabarkan dari silabus","1-4 mata pelajaran memiliki RPP dengan mengintegrasikan pendidikan karakter yang dijabarkan dari silabus","Tidak ada mata pelajaran memiliki RPP dengan mengintegrasikan pendidikan karakter yang dijabarkan dari silabus "]'),
(20, 'RPP disusun dengan memperhatikan 6 prinsip penyusunan.', '["91%-100% RPP sudah 6 prinsip penyusunan.","81%-90% RPP sudah 6 prinsip penyusunan.","71%-80% RPP sudah 6 prinsip penyusunan.","61%-70% RPP sudah 6 prinsip penyusunan.","Kurang dari 60% RPP sudah 6 prinsip penyusunan. "]'),
(21, 'Program keahlian melaksanakan proses pembelajaran sesuai dengan persyaratan yang ditentukan.', '["Memenuhi 4 persyaratan pelaksanaan proses pembelajaran","Memenuhi 3 persyaratan pelaksanaan proses pembelajaran","Memenuhi 2 persyaratan pelaksanaan proses pembelajaran","Memenuhi 1 persyaratan pelaksanaan proses pembelajaran","Tidak memenuhi persyaratan pelaksanaan proses pembelajaran   "]'),
(22, 'Guru melaksanakan proses pembelajaran sesuai dengan langkah-langkah pembelajaran yang tertuang dalam RPP.', '["91%-100% guru melaksanakan proses pembelajaran sesuai dengan langkah-langkah pembelajaran","81%-90% guru melaksanakan proses pembelajaran sesuai dengan langkah-langkah pembelajaran","71%-80% guru melaksanakan proses pembelajaran sesuai dengan langkah-langkah pembelajaran","61%-70% guru melaksanakan proses pembelajaran sesuai dengan langkah-langkah pembelajaran","Kurang dari 61% guru melaksanakan proses pembelajaran sesuai dengan langkah-langkah pembelajaran "]'),
(23, 'Program keahlian melaksanakan proses pembelajaran berbasis Teknologi Informasi dan Komunikasi (sesuai dengan tingkat kebutuhan Program keahliannya) dengan SDM yang memiliki kesesuaian kompetensi.', '["Pelaksanaan pembelajaran berbasis TIK dilakukan sendiri oleh pihak program keahlian dengan SDM sendiri yang memiliki kesesuaian kompetensi dan bersertifikat","Pelaksanaan pembelajaran berbasis TIK dilakukan sendiri oleh pihak program keahlian dengan mendatangkan SDM dari sekolah/madrasah lain","Pelaksanaan pembelajaran berbasis TIK dilakukan oleh pihak lain dengan sebagian SDM dari pihak program keahlian","Pelaksanaan pembelajaran berbasis TIK dilakukan oleh pihak lain (outsource)","Tidak melaksanakan pembelajaran berbasis TIK   "]'),
(24, 'Kegiatan wirausaha pada unit produksi atau business center dilakukan sebagai wahana untuk meningkatkan kompetensi siswa sesuai dengan keahliannya.', '["Kegiatan wirausaha pada unit produksi atau business center dilakukan sesuai dengan proses pembelajaran, materi program keahlian, dan aturan pengelolaan yang transparan","Kegiatan wirausaha pada unit produksi atau business center dilakukan sesuai dengan proses pembelajaran, dan materi program keahlian","Kegiatan wirausaha pada unit produksi atau business center dilakukan sesuai dengan proses pembelajaran dan aturan pengelolaan yang transparan","Kegiatan wirausaha pada unit produksi atau business center dilakukan hanya sesuai dengan proses pembelajaran.","Tidak sesuai dengan proses pembelajaran "]'),
(25, 'Program keahlian menindaklanjuti Memorandum of Understanding (MoU) kerjasama dengan DU/DI.', '["91%-100% tindaklanjut MoU melalui pelaksanaan pembelajaran","81%-90% tindaklanjut MoU melalui pelaksanaan pembelajaran","71%-80% tindaklanjut MoU melalui pelaksanaan pembelajaran","61%-70% tindaklanjut MoU melalui pelaksanaan pembelajaran","Kurang dari 61% tindaklanjut MoU melalui pelaksanaan pembelajaran "]'),
(26, 'Siswa melakukan praktik kerja pada DU/DI untuk memperoleh kompetensi keahliannya.', '["96%-100% siswa melakukan prakerin pada DU/DI yang relevan","91%-95% siswa melakukan prakerin pada DU/DI yang relevan","86%-90% siswa melakukan prakerin pada DU/DI yang relevan","81%-85% siswa melakukan prakerin pada DU/DI yang relevan","Kurang dari 81% siswa melakukan prakerin pada DU/DI yang relevan   "]'),
(27, 'Kepala sekolah/madrasah dan program keahlian melakukan pemantauan proses pembelajaran mencakup tiga tahapan yaitu: (1) perencanaan, (2) pelaksanaan, dan (3) penilaian hasil pembelajaran.', '["Mencakup 3 tahap pemantauan serta dilakukan diskusi hasil pemantauan","Mencakup 3 tahap pemantauan tanpa dilakukan diskusi hasil pemantauan","Mencakup 2 tahap pemantauan","Mencakup 1 tahap pemantauan","Tidak pernah melakukan pemantauan "]'),
(28, 'Supervisi proses pembelajaran dilakukan oleh kepala sekolah/madrasah dan ketua program keahlian dengan cara pemberian contoh, diskusi, pelatihan, dan konsultasi.', '["Melakukan supervisi proses pembelajaran melalui 4 cara","Melakukan supervisi proses pembelajaran melalui 3 cara","Melakukan supervisi proses pembelajaran melalui 2 cara","Melakukan supervisi proses pembelajaran melalui 1 cara","Tidak melakukan supervisi proses pembelajaran "]'),
(29, 'Evaluasi terhadap guru dalam proses pembelajaran dilakukan oleh kepala sekolah/madrasah dan ketua program keahlian dengan memperhatikan 4 aspek, yaitu 1) persiapan, 2) pelaksanaan, 3) evaluasi pembelajaran, dan 4) rencana tindak lanjut.', '["Evaluasi dilakukan dengan memperhatikan 4 aspek","Evaluasi dilakukan dengan memperhatikan 3 aspek","Evaluasi dilakukan dengan memperhatikan 2 aspek","Evaluasi dilakukan dengan memperhatikan 1 aspek","Tidak melakukan evaluasi   "]'),
(30, 'Kepala sekolah/madrasah dan ketua program keahlian menyampaikan hasil pengawasan (pemantauan, supervisi, dan evaluasi) proses pembelajaran kepada pemangku kepentingan.', '["Hasil pengawasan disampaikan kepada guru yang bersangkutan, dewan guru, dan pengawas sekolah/madrasah","Hasil pengawasan disampaikan kepada guru yang bersangkutan dan dewan guru","Hasil pengawasan disampaikan kepada yang bersangkutan saja","Tidak disampaikan","Tidak melakukan pengawasan "]'),
(31, 'Kepala sekolah/madrasah dan ketua program keahlian melakukan tindak lanjut terhadap hasil pengawasan proses pembelajaran.', '["91%-100% hasil pengawasan selama 1 tahun terakhir dilakukan tindak lanjut","81%-90% hasil pengawasan selama 1 tahun terakhir dilakukan tindak lanjut","71%-80% hasil pengawasan selama 1 tahun terakhir dilakukan tindak lanjut","61%-70% hasil pengawasan selama 1 tahun terakhir dilakukan tindak lanjut","Kurang dari 61% hasil pengawasan selama 1 tahun terakhir dilakukan tindak lanjut   "]'),
(32, 'Siswa memperoleh pengalaman belajar melalui kelompok mata pelajaran Iptek untuk dapat berpikir logis, kritis, kreatif, dan inovatif dalam pengambilan keputusan dan pemecahan masalah.', '["91%-100% mata pelajaran kelompok Iptek memuat tugas terstruktur","81%-90% mata pelajaran kelompok Iptek memuat tugas terstruktur","71%-80% mata pelajaran kelompok Iptek memuat tugas terstruktur","61%-70% mata pelajaran kelompok Iptek memuat tugas terstruktur","Kurang dari 61% mata pelajaran kelompok Iptek memuat tugas terstruktur "]'),
(33, 'Siswa terlibat dalam kegiatan belajar kelompok mata pelajaran Iptek yang berkaitan dengan analisis dan pemecahan masalah kompleks.', '["4 atau lebih mata pelajaran kelompok Iptek dapat menunjukkan kegiatan pembelajaran yang berkaitan dengan pemecahan masalah kompleks.","3 mata pelajaran kelompok Iptek dapat menunjukkan kegiatan pembelajaran yang berkaitan dengan pemecahan masalah kompleks.","2 mata pelajaran kelompok Iptek dapat menunjukkan kegiatan pembelajaran yang berkaitan dengan pemecahan masalah kompleks.","1 mata pelajaran kelompok Iptek dapat menunjukkan kegiatan pembelajaran yang berkaitan dengan pemecahan masalah kompleks.","Tidak ada mata pelajaran kelompok Iptek dapat menunjukkan kegiatan pembelajaran yang berkaitan dengan pemecahan masalah kompleks.   "]'),
(34, 'Siswa terlibat dalam kegiatan belajar kelompok mata pelajaran Iptek yang memiliki kemampuan untuk menganalisis gejala alam dan sosial.', '["4 atau lebih mata pelajaran kelompok Iptek dapat menunjukkan kegiatan pembelajaran untuk menganalisis gejala alam dan sosial.","3 mata pelajaran kelompok Iptek dapat menunjukkan kegiatan pembelajaran untuk menganalisis gejala alam dan sosial.","2 mata pelajaran kelompok Iptek dapat menunjukkan kegiatan pembelajaran untuk menganalisis gejala alam dan sosial.","1 mata pelajaran kelompok Iptek dapat menunjukkan kegiatan pembelajaran untuk menganalisis gejala alam dan sosial.","Tidak ada mata pelajaran kelompok Iptek dapat menunjukkan kegiatan pembelajaran untuk menganalisis gejala alam dan sosial. "]'),
